// Geli≈ümi≈ü Test Dosyasƒ± - Karma≈üƒ±k JavaScript S√ºre√ßlerini Test Etmek ƒ∞√ßin
console.log("üöÄ Geli≈ümi≈ü Test Ba≈ülatƒ±lƒ±yor...");

// 1. Sƒ±nƒ±f Tanƒ±mlamasƒ± ve Kalƒ±tƒ±m
class BaseProcessor {
    constructor(name) {
        this.name = name;
        this.startTime = Date.now();
        console.log(`üì¶ ${this.name} i≈ülemcisi olu≈üturuldu`);
    }

    async process(data) {
        console.log(`‚öôÔ∏è ${this.name} i≈ülem ba≈ülatƒ±ldƒ±...`);
        await this.delay(Math.random() * 1000 + 500);
        return `${this.name} tarafƒ±ndan i≈ülendi: ${data}`;
    }

    delay(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    getExecutionTime() {
        return Date.now() - this.startTime;
    }
}

class AdvancedProcessor extends BaseProcessor {
    constructor(name, complexity = 1) {
        super(name);
        this.complexity = complexity;
        this.processedCount = 0;
    }

    async process(data) {
        console.log(`üî• Geli≈ümi≈ü i≈ülem ba≈ülatƒ±ldƒ± (Karma≈üƒ±klƒ±k: ${this.complexity})`);
        
        // Karma≈üƒ±k hesaplama sim√ºlasyonu
        for (let i = 0; i < this.complexity * 1000; i++) {
            Math.sqrt(Math.random() * 1000);
        }

        const result = await super.process(data);
        this.processedCount++;
        
        console.log(`‚úÖ ƒ∞≈ülem tamamlandƒ±. Toplam i≈ülenen: ${this.processedCount}`);
        return `[ADVANCED] ${result}`;
    }

    async batchProcess(dataArray) {
        console.log(`üìä Toplu i≈ülem ba≈ülatƒ±ldƒ±: ${dataArray.length} √∂ƒüe`);
        const results = [];
        
        for (const data of dataArray) {
            try {
                const result = await this.process(data);
                results.push({ success: true, data: result });
            } catch (error) {
                results.push({ success: false, error: error.message });
                console.error(`‚ùå ƒ∞≈ülem hatasƒ±: ${error.message}`);
            }
        }
        
        return results;
    }
}

// 2. Promise Chain ve Async/Await Karƒ±≈üƒ±mƒ±
async function complexAsyncOperation() {
    console.log("üîÑ Karma≈üƒ±k async i≈ülem ba≈ülatƒ±ldƒ±...");
    
    const operations = [
        () => fetchData("kullanici-verileri"),
        () => processUserData(),
        () => validateResults(),
        () => saveToDatabase()
    ];

    let result = null;
    for (const operation of operations) {
        try {
            result = await operation();
            console.log(`‚úì ƒ∞≈ülem tamamlandƒ±: ${operation.name}`);
        } catch (error) {
            console.error(`‚ùå ƒ∞≈ülem hatasƒ±: ${error.message}`);
            throw error;
        }
    }
    
    return result;
}

// 3. Sim√ºle edilmi≈ü API √ßaƒürƒ±larƒ±
async function fetchData(endpoint) {
    console.log(`üåê API √ßaƒürƒ±sƒ± yapƒ±lƒ±yor: ${endpoint}`);
    await new Promise(resolve => setTimeout(resolve, Math.random() * 800 + 200));
    
    if (Math.random() < 0.1) {
        throw new Error(`API hatasƒ±: ${endpoint} eri≈üilemez`);
    }
    
    return {
        endpoint,
        data: `${endpoint} verisi`,
        timestamp: new Date().toISOString(),
        requestId: Math.random().toString(36).substring(7)
    };
}

async function processUserData() {
    console.log("üë§ Kullanƒ±cƒ± verileri i≈üleniyor...");
    await new Promise(resolve => setTimeout(resolve, 300));
    return { processed: true, users: Math.floor(Math.random() * 100) };
}

async function validateResults() {
    console.log("üîç Sonu√ßlar doƒürulanƒ±yor...");
    await new Promise(resolve => setTimeout(resolve, 200));
    
    if (Math.random() < 0.05) {
        throw new Error("Doƒürulama hatasƒ±: Ge√ßersiz veri formatƒ±");
    }
    
    return { valid: true, score: Math.random() * 100 };
}

async function saveToDatabase() {
    console.log("üíæ Veritabanƒ±na kaydediliyor...");
    await new Promise(resolve => setTimeout(resolve, 400));
    return { saved: true, recordId: Date.now() };
}

// 4. Event Emitter Sim√ºlasyonu
class EventProcessor {
    constructor() {
        this.listeners = {};
        this.eventCount = 0;
    }

    on(event, callback) {
        if (!this.listeners[event]) {
            this.listeners[event] = [];
        }
        this.listeners[event].push(callback);
    }

    emit(event, data) {
        console.log(`üì° Event yayƒ±nlandƒ±: ${event}`);
        this.eventCount++;
        
        if (this.listeners[event]) {
            this.listeners[event].forEach(callback => {
                try {
                    callback(data);
                } catch (error) {
                    console.error(`Event handler hatasƒ±: ${error.message}`);
                }
            });
        }
    }

    getEventCount() {
        return this.eventCount;
    }
}

// 5. Ana Test Fonksiyonu
async function runAdvancedTests() {
    console.log("\n" + "=".repeat(50));
    console.log("üß™ GELƒ∞≈ûMƒ∞≈û TEST S√úƒ∞Tƒ∞ BA≈ûLATIILIYOR");
    console.log("=".repeat(50));

    try {
        // Test 1: Sƒ±nƒ±f ve Kalƒ±tƒ±m Testi
        console.log("\nüìã Test 1: Sƒ±nƒ±f ƒ∞≈ülemleri");
        const processor = new AdvancedProcessor("TestProcessor", 3);
        const singleResult = await processor.process("test-verisi-1");
        console.log(`Sonu√ß: ${singleResult}`);

        // Test 2: Toplu ƒ∞≈ülem Testi
        console.log("\nüìã Test 2: Toplu ƒ∞≈ülemler");
        const testData = ["veri-1", "veri-2", "veri-3", "veri-4", "veri-5"];
        const batchResults = await processor.batchProcess(testData);
        console.log(`Toplu i≈ülem tamamlandƒ±: ${batchResults.length} sonu√ß`);

        // Test 3: Karma≈üƒ±k Async ƒ∞≈ülemler
        console.log("\nüìã Test 3: Karma≈üƒ±k Async ƒ∞≈ülemler");
        const complexResult = await complexAsyncOperation();
        console.log(`Karma≈üƒ±k i≈ülem sonucu:`, complexResult);

        // Test 4: Event System Testi
        console.log("\nüìã Test 4: Event Sistemi");
        const eventProcessor = new EventProcessor();
        
        eventProcessor.on('test-event', (data) => {
            console.log(`üéØ Event alƒ±ndƒ±: ${JSON.stringify(data)}`);
        });
        
        eventProcessor.on('error-event', (error) => {
            console.log(`‚ö†Ô∏è Hata eventi: ${error}`);
        });

        // Event'leri tetikle
        for (let i = 0; i < 5; i++) {
            eventProcessor.emit('test-event', { 
                id: i, 
                message: `Test mesajƒ± ${i + 1}`,
                timestamp: Date.now()
            });
            await new Promise(resolve => setTimeout(resolve, 100));
        }

        // Test 5: Paralel ƒ∞≈ülemler
        console.log("\nüìã Test 5: Paralel ƒ∞≈ülemler");
        const parallelTasks = [
            fetchData("endpoint-1"),
            fetchData("endpoint-2"),
            fetchData("endpoint-3")
        ];

        const parallelResults = await Promise.allSettled(parallelTasks);
        console.log(`Paralel i≈ülemler tamamlandƒ±: ${parallelResults.length} sonu√ß`);

        // √ñzet
        console.log("\n" + "=".repeat(50));
        console.log("üìä TEST SONU√áLARI");
        console.log("=".repeat(50));
        console.log(`‚úÖ ƒ∞≈ülem s√ºresi: ${processor.getExecutionTime()}ms`);
        console.log(`‚úÖ ƒ∞≈ülenen veri sayƒ±sƒ±: ${processor.processedCount}`);
        console.log(`‚úÖ Event sayƒ±sƒ±: ${eventProcessor.getEventCount()}`);
        console.log(`‚úÖ Paralel g√∂rev sayƒ±sƒ±: ${parallelResults.length}`);
        console.log("üéâ T√ºm testler ba≈üarƒ±yla tamamlandƒ±!");

    } catch (error) {
        console.error("‚ùå Test hatasƒ±:", error.message);
        console.error("Stack trace:", error.stack);
    }
}

// 6. Error Handling ve Cleanup
process.on('uncaughtException', (error) => {
    console.error('üö® Yakalanmamƒ±≈ü hata:', error.message);
    process.exit(1);
});

process.on('unhandledRejection', (reason, promise) => {
    console.error('üö® ƒ∞≈ülenmemi≈ü Promise reddi:', reason);
});

// Ana fonksiyonu √ßalƒ±≈ütƒ±r
if (require.main === module) {
    runAdvancedTests()
        .then(() => {
            console.log("\nüèÅ Test suite tamamlandƒ±!");
            process.exit(0);
        })
        .catch((error) => {
            console.error("üí• Fatal hata:", error);
            process.exit(1);
        });
}

module.exports = {
    BaseProcessor,
    AdvancedProcessor,
    EventProcessor,
    runAdvancedTests,
    fetchData,
    complexAsyncOperation
};
